import React, { useMemo } from "react";
import TableCell from "./TableCell/TableCell";
import StaticCell from "./TableCell/StaticCell";

const TableRow = ({ rowCount, data }) => {
   const getRowLabel = () => <StaticCell key={rowCount} value={rowCount} />;
   const getRowCells = () => {
      return data.map((cellData) => (
         <TableCell key={cellData.id} id={cellData.id} value={cellData.value} />
      ));
   };
   const sumRowCell = useMemo(() => {
      const sumRow = data
         .map((data) => data.value)
         .reduce((sum = 0, value) => sum + value);
      return <StaticCell key={sumRow} value={sumRow} />;
   }, [getRowCells()]);
   const cells = [getRowLabel(), ...getRowCells(), sumRowCell];

   return <tr>{cells}</tr>;
};

export default TableRow;
