import React from "react";
import "./InputCounts.scss";
import InputControl from "./InputControl";
import Button from "../../../Button";

const InputCounts = ({ id, label, value, handleChange, controlHandler }) => {
   const controlClickHandler = (type) => {
      if (type === "+") {
         controlHandler(++value);
      } else if (type === "-") {
         controlHandler(--value);
      }
   };
   return (
      <div className="input-settings">
         <Button className='' buttonText='-' onClickHandle={()=>controlClickHandler('decrement')}/>
         {/* <InputControl type="-" controlClickHandler={} /> */}
         <input
            className="input-settings__input"
            type="number"
            id={id}
            onChange={handleChange}
            min="0"
            value={value}
         />
         <InputControl type="+" controlClickHandler={controlClickHandler} />
      </div>
   );
};

export default InputCounts;
