import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import HeadCell from "./HeadCell/index";

const TableHead = () => {
   const columns = useSelector((state) => state.matrix.settings.columns);
   const createHeader = () => {
      let heads = [];
      for (let i = 0; i < columns; i++) {
         if (i === 0) {
            heads.push(<HeadCell key={i} value="" />);
         } else {
            heads.push(<HeadCell key={i} value={"col " + i} />);
         }
      }
      return heads;
   };

   useEffect(() => {
      const heads = createHeader();
   }, []);

   return (
      <thead>
         <tr>{heads}</tr>
      </thead>
   );
};

export default TableHead;
