import React from "react";
import { useDispatch, useSelector } from "react-redux";
import {
   incrementCell,
   resetNearestIds,
   setNearestIds,
} from "../../../../../../../redux/actions";

const IncrementCell = ({ id, value, cellsSetting }) => {
   const nearestIds = useSelector((state) => state.matrix.nearestIds);
   const dispatch = useDispatch();
   const isNearest =
      nearestIds.length > 0 && nearestIds.find((nearestId) => nearestId === id)
         ? true
         : false;

   return (
      <div
         style={isNearest ? { backgroundColor: "aqua" } : null}
         onMouseEnter={() => dispatch(setNearestIds(cellsSetting, value))}
         onMouseLeave={() => dispatch(resetNearestIds())}
         onClick={() => dispatch(incrementCell(id, value))}
      >
         {value}
      </div>
   );
};

export default IncrementCell;
