import React from "react";
import "./InputCounts.scss";
import InputControl from "./InputControl/";
import { useDispatch } from "react-redux";
import { incrementInput } from "../../../redux/actions";

const InputCounts = ({ id, label, value, handleChange }) => {
   const dispatch = useDispatch();
   const controlClickHandler = (type) => {
      if (type === "+") {
         dispatch(incrementInput(id));
      }
   };
   return (
      <div className="input-wrapper">
         <label htmlFor={id}>{label}</label>
         <InputControl type="-" controlClickHandler={controlClickHandler} />
         <input
            className="input-settings"
            type="number"
            id={id}
            onChange={handleChange}
            min="0"
            value={value}
         />
         <InputControl type="+" />
      </div>
   );
};

export default InputCounts;
