import {
   INCREMENT_CELL,
   REMOVE_ROW,
   ADD_ROW,
   SET_NEAREST_IDS,
   RESET_NEAREST_IDS,
   SET_SETTINGS,
   RESET_SETTINGS,
} from "../action_types";
import {
   createMatrix,
   rebuildMatrix,
   addRows,
} from "./matrixReducer/buildingMatrix";
import defaultState from "./matrixReducer/defaultState";
import getIds from "./matrixReducer/findNearestIds";

const initialState = defaultState;
const sortData = (data) => {
   if (data.length > 0) {
      return data.flat().sort((a, b) => a.value - b.value);
   }
};
const checkMatrixData = (data, settings, newSettings) => {
   if (data.length === 0) {
      return createMatrix(newSettings.rowsCount, newSettings.columnsCount);
   } else if (
      settings.rowsCount !== newSettings.rowsCount ||
      settings.columnsCount !== newSettings.columnsCount
   ) {
      return rebuildMatrix(data, settings, newSettings);
   }
};
const incrementDataCell = (data, id) => {
   let newData = [...data];
   newData.find((row) =>
      row.find((item) => (item.id === id ? item.value++ : null))
   );
   return newData;
};
const matrixReducer = (state = initialState, action) => {
   switch (action.type) {
      case SET_SETTINGS:
         const newSettings = { ...action.settings };
         return {
            ...state,
            data: checkMatrixData([...state.data]),
            sortedData: sortData(data),
            settings: newSettings,
         };
      case RESET_SETTINGS:
         return {
            defaultState,
         };
      case ADD_ROW:
         return {
            ...state,
            settings: { ...state.settings, rowsCount: state.data.length },
            data: addRows([...state.data], 1, state.settings.columnsCount),
            sortedData: sortData(state.data),
         };
      case REMOVE_ROW:
         return {
            ...state,
            settings: { ...state.settings, rowsCount: state.data.length },
            data: state.data.filter((row, index) => index !== action.rowIndex),
            sortedData: sortData(state.data),
         };
      case INCREMENT_CELL:
         return {
            ...state,
            data: incrementDataCell(state.data, action.id),
            sortedData: sortData(state.data),
            nearestIds: getIds(
               state.sortedData,
               state.settings.cellsCount,
               action.id
            ),
         };
      case SET_NEAREST_IDS:
         if (state.settings.cellsCount) {
            return {
               ...state,
               sortedData: sortData(state.data),
               nearestIds: getIds(
                  state.sortedData,
                  state.settings.cellsCount,
                  action.id
               ),
            };
         } else {
            return { ...state };
         }

      case RESET_NEAREST_IDS:
         if (!!state.nearestIds.ids.length) {
            return { ...state, nearestIds: { sameValueIds: [], ids: [] } };
         } else {
            return { ...state };
         }
      default:
         return defaultState;
   }
};

export default matrixReducer;
