import React from "react";
import { useDispatch, useSelector } from "react-redux";
import {
   incrementCell,
   setNearestValues,
} from "../../../../../../../redux/actions";

const IncrementCell = ({ id, value, cellsSetting }) => {
   const nearestIds = useSelector((state) => state.matrix.nearestIds);
   const isNearest = nearestIds.find((id) => id === id) ? true : false;

   let style = isNearest ? { backgroundColor: "aqua" } : null;
   console.log(cellsSetting);

   return (
      <div
         style={style}
         onMouseEnter={() => dispatch(setNearestValues(cellsSetting, value))}
         onMouseLeave={() => (style = null)}
         onClick={() => dispatch(incrementCell(id, value))}
      >
         {value}
      </div>
   );
};

export default IncrementCell;
