import React, { useState } from "react";
import InputCounts from "./InputCounts";
import "./StartPage.scss";

const StartPage = () => {
   const [rows, setRows] = useState(0);
   const [columns, setColumns] = useState(0);
   const [cells, setCells] = useState(0);

   const handleChangeRows = (event) => {
      setRows(event.target.value);
   };

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            handleChange={handleChangeRows}
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            handleChange={handleChangeColumns}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            handleChange={handleChangeCells}
         />

         {/* <button onClick=></button> */}
      </div>
   );
};

export default StartPage;
