import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
   incrementCell,
   setNearestValues,
} from "../../../../../../../redux/actions";

const IncrementCell = ({ id, value, cellsSetting }) => {
   const nearestIds = useSelector((state) => state.matrix.nearestIds);
   const dispatch = useDispatch();
   const [style, setStyle] = useState(null);

   const isNearest = nearestIds.find((nearestId) => nearestId === id)
      ? setStyle({ backgroundColor: "aqua" })
      : null;

   return (
      <div
         style={style}
         onMouseEnter={() => dispatch(setNearestValues(cellsSetting, value))}
         onMouseLeave={() => {
            setStyle(null);
         }}
         onClick={() => dispatch(incrementCell(id, value))}
      >
         {value}
      </div>
   );
};

export default IncrementCell;
