import React, { useState, useEffect } from "react";
import InputCounts from "./InputCounts";
import "./StartPage.scss";

const StartPage = () => {
   const [rows, setRows] = useState(0);
   const [columns, setColumns] = useState(0);
   const [cells, setCells] = useState(0);

   const handleChangeRows = (event) => {
      setRows(event.target.value);
   };
   const handleChangeColumns = (event) => {
      setColumns(event.target.value);
   };
   const handleChangeCells = (event) => {
      setCells(event.target.value);
   };
   const createMatrix = (rowsCount, columnsCount, cellsCount) => {
      let matrix = [];
      const createRows = () => {
         for (let i = 0; i < rowsCount; i++) {
            const row = [];
            matrix.push(row);
         }
      };
      const createColumns = () => {
         matrix.forEach((item, index) => {
            for (let i = 0; i < columnsCount; i++) {
               const cellObject = {
                  id: String(index + 1).concat(i + 1),
                  value: Math.random()*100 + 1,
               };
               item.push(cellObject);
            }
         });
      };

      createRows();
      createColumns();

      console.log(matrix);
   };

   return (
      <div className="start-page">
         <h1>Welcome to Matrix Builder</h1>
         <InputCounts
            id="rows"
            label="Set the rows count"
            handleChange={handleChangeRows}
         />
         <InputCounts
            id="columns"
            label="Set the columns count"
            handleChange={handleChangeColumns}
         />
         <InputCounts
            id="cells"
            label="Set the count of cells to show the nearest value"
            handleChange={handleChangeCells}
         />

         <button onClick={() => createMatrix(rows, columns, cells)}>
            Apply settings of matrix
         </button>
      </div>
   );
};

export default StartPage;
