import {
   CELL_INCREMENT,
   CREATE_MATRIX,
   SET_MATRIX_COLUMNS,
   SET_MATRIX_ROWS,
} from "./types";

export const setMatrixRows = (value) => {
   return {
      type: SET_MATRIX_ROWS,
      payload: value,
   };
};

export const setMatrixColumns = (value) => {
   return {
      type: SET_MATRIX_COLUMNS,
      payload: value,
   };
};

export const createMatrix = () => {
   return {
      type: CREATE_MATRIX,
   };
};

export const cellIncrement = (id) => {
   return {
      type: CELL_INCREMENT,
      payload: id,
   };
};
