import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { cellRenderers } from "../../../../../CellRenderers";
import { removeRow, setNearestValues } from "../../../../../redux/actions";
import CrossButton from "../../../../CrossButton";
import TableCell from "./TableCell";

const TableRow = ({ rowCount, data, cellsSetting }) => {
   const matrixData = useSelector((state) => state.matrix.data);
   const nearestValues = useSelector((state) => state.matrix.nearestValues);
   const dispatch = useDispatch();
   const [sumIsHover, setSumIsHover] = useState(false);

   //TODO: move to the redux in the store
   const findNearestValues = (cellsCount, currentValue) => {
      if (+cellsCount === 0) {
         return;
      } else {
         let sortedMatrix = matrixData
            .reduce((currentRow, nextRow) => currentRow.concat(nextRow))
            .sort((a, b) => a.value - b.value);
         let nearestIds = [];
         const iterationCount =
            cellsCount < sortedMatrix.length
               ? cellsCount
               : sortedMatrix.length - 1;
         let indexCurrentValue = sortedMatrix.indexOf(
            sortedMatrix.find((item) => item.value === currentValue)
         );
         const exclusionIds = (indexCurrentValue) =>
            sortedMatrix
               .filter((item, index) => index !== indexCurrentValue)
               .forEach((item) => nearestIds.push(item.id));
         if (indexCurrentValue === 0) {
            sortedMatrix.length - 1 === iterationCount
               ? exclusionIds(indexCurrentValue)
               : sortedMatrix
                    .splice(1, iterationCount)
                    .forEach((item) => nearestIds.push(item.id));
         } else if (indexCurrentValue === sortedMatrix.length - 1) {
            sortedMatrix.length - 1 === iterationCount
               ? exclusionIds(indexCurrentValue)
               : sortedMatrix
                    .splice(-iterationCount - 1, iterationCount)
                    .forEach((item) => nearestIds.push(item.id));
         } else {
            if (sortedMatrix.length - 1 === iterationCount) {
               exclusionIds(indexCurrentValue);
            } else {
               for (let i = 0; i < iterationCount; i++) {
                  const sortedValues = sortedMatrix
                     .map((data) => data.value)
                     .sort((a, b) => a - b);
                  indexCurrentValue = sortedValues.indexOf(currentValue);
                  if (indexCurrentValue === 0) {
                     sortedMatrix
                        .splice(1, i)
                        .forEach((item) => nearestIds.push(item.id));
                  } else if (index === sortedMatrix.length - 1) {
                     sortedMatrix
                        .splice(-count - 1, count)
                        .forEach((item) => nearestIds.push(item.id));
                  } else {
                     const prevValue = sortedValues[indexCurrentValue - 1];
                     const nextValue = sortedValues[indexCurrentValue + 1];
                     const prevDiff = Math.abs(currentValue - prevValue);
                     const nextDiff = Math.abs(nextValue - currentValue);
                     const nearestValue =
                        prevDiff < nextDiff ? prevValue : nextValue;
                     const nearestId = sortedMatrix.find(
                        (item) => item.value === nearestValue
                     ).id;
                     nearestIds.push(nearestId);
                     sortedMatrix.splice(
                        sortedMatrix.indexOf(
                           sortedMatrix.find((item) => item.id === nearestId)
                        ),
                        1
                     );
                     console.log(i, nearestValue, nearestId, sortedValues);
                     console.log(nearestIds);
                  }
               }
            }
         }
         return dispatch(setNearestValues(nearestIds));
      }
   };
   const rowLabel = (
      <TableCell
         key={rowCount}
         value={rowCount}
         Renderer={cellRenderers.static}
      />
   );
   const rowCells = () => {
      const renderer = sumIsHover
         ? cellRenderers.percentage
         : cellRenderers.increment;

      return data.map((cellData, index) => {
         const percentOfSum = Math.round((cellData.value * 100) / sumRow);
         // const isNearest
         return (
            <TableCell
               key={cellData.id}
               id={cellData.id}
               value={cellData.value}
               percent={percentOfSum}
               cellHoverHandler={() =>
                  findNearestValues(cellsSetting, cellData.value)
               }
               Renderer={renderer}
            />
         );
      });
   };
   const sumRow = data
      .map((data) => data.value)
      .reduce((sum = 0, value) => sum + value);
   const sumRowCell = (
      <TableCell
         key={sumRow}
         value={sumRow}
         Renderer={cellRenderers.sum}
         sumMouseEnterHandler={() => setSumIsHover(true)}
         sumMouseLeaveHandler={() => setSumIsHover(false)}
      />
   );
   const cells = [rowLabel, ...rowCells(), sumRowCell];

   return (
      <tr>
         {cells}
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowCount))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
