import {
   INCREMENT_CELL,
   CREATE_MATRIX,
   REMOVE_ROW,
   SET_MATRIX_COLUMNS,
   SET_MATRIX_ROWS,
} from "./types";

export const setMatrixRows = (value) => {
   return {
      type: SET_MATRIX_ROWS,
      payload: value,
   };
};

export const setMatrixColumns = (value) => {
   return {
      type: SET_MATRIX_COLUMNS,
      payload: value,
   };
};

export const createMatrix = () => {
   return {
      type: CREATE_MATRIX,
   };
};

export const incrementCell = (id, value) => {
   return {
      type: INCREMENT_CELL,
      id,
      value,
   };
};

export const removeRow = (rowCount) => {
   return {
      type: REMOVE_ROW,
      index: rowCount - 1,
   };
};
