import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { removeRow } from "../../../../redux/actions";
import CrossButton from "../../../../CrossButton";
import IncrementCell from "./CellRenderer/IncrementCell";
import PercentageCell from "./CellRenderer/PercentageCell";
import StaticCell from "./CellRenderer/StaticCell";
import SumCell from "./CellRenderer/SumCell";

const TableRow = ({ rowNumber, data }) => {
   const dispatch = useDispatch();
   const [sumIsHover, setSumIsHover] = useState(false);
   const sumRow = data
      .map((data) => data.value)
      .reduce((sum = 0, value) => sum + value);

   const rowLabel = (
      <StaticCell
         key={rowNumber}
         value={rowNumber}
      />
   );
   const rowRenderer = (cellData) => {
      return sumIsHover ? (
         <PercentageCell
            key={cellData.id}
            value={cellData.value}
            sumRow={sumRow}
         />
      ) : (
         <IncrementCell
            key={cellData.id}
            id={cellData.id}
            value={cellData.value}
         />
      );
   };
   const rowCells = () => {
      return data.map((cellData) => rowRenderer(cellData));
   };

   const sumRowCell = (
      <SumCell
         key={sumRow}
         value={sumRow}
         sumMouseEnterHandler={() => setSumIsHover(true)}
         sumMouseLeaveHandler={() => setSumIsHover(false)}
      />
   );
   const cells = [rowLabel, ...rowCells(), sumRowCell];

   return (
      <tr>
         {cells}
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowNumber))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
