import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const AverageRow = () => {
   const data = useSelector((state) =>
      state.matrix.data.map((item) =>
         item.filter((data) => data.value).map((row) => row.value)
      )
   );
   const columnsCount = useSelector((state) => state.matrix.settings.columns);
   const [averageValues, setAverageValues] = useState([]);

   useEffect(() => {
      const averageSum = [];
      //current row values and next row values return the sum of values by equal index
      const columns = data.reduce((current, next) => {
         return current.map((value, index) => {
            return value + next[index];
         });
      });
      console.log(columns);
   }, [data]);

   return <tr></tr>;
};

export default AverageRow;
