import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { cellRenderers } from "../../../../../CellRenderers";
import { removeRow } from "../../../../../redux/actions";
import CrossButton from "../../../../CrossButton";
import IncrementCell from "./TableCell/CellRenderer/IncrementCell";
import PercentageCell from "./TableCell/CellRenderer/PercentageCell";
import StaticCell from "./TableCell/CellRenderer/StaticCell";

const TableRow = ({ rowCount, data }) => {
   const dispatch = useDispatch();
   const [sumIsHover, setSumIsHover] = useState(false);
   const sumRow = data
      .map((data) => data.value)
      .reduce((sum = 0, value) => sum + value);

   const rowLabel = (
      <StaticCell
         key={rowCount}
         value={rowCount}
         Renderer={cellRenderers.static}
      />
   );
   const rowCells = () => {
      const Renderer = (cellData) =>
         sumIsHover ? (
            <PercentageCell key={cellData.id} sumRow={sumRow} />
         ) : (
            <IncrementCell key={cellData.id} />
         );
      return data.map((cellData) => Renderer(cellData));
   };

   const sumRowCell = (
      <StaticCell
         value={sumRow}
         sumMouseEnterHandler={() => setSumIsHover(true)}
         sumMouseLeaveHandler={() => setSumIsHover(false)}
      />
   );
   const cells = [rowLabel, ...rowCells(), sumRowCell];

   return (
      <tr>
         {cells}
         <td>
            <CrossButton
               buttonClickHandler={() => dispatch(removeRow(rowCount))}
            />
         </td>
      </tr>
   );
};

export default TableRow;
