const calculateAverageValues = (matrixData) => {
   const rowsValues = matrixData.map((item) => item.map((row) => row.value));
   let averageValues = [];
   if (!!rowsValues) {
      for (let i = 0; i < rowsValues[i].length; i++) {
         const columnValues = rowsValues
            .map((row) => row[i])
            .reduce((sum, value) => sum + value, 0);
         const average = columnValues / matrixData.length;
         averageValues.push(average);
      }
   }
   return averageValues;
};
export default calculateAverageValues;

