const calculateAverageValues = (values) => {
   let averageValues = [];
   for (let i = 0; i < values.length - 1; i++) {
      let columnValues = values
         .map((row) => row[i])
         .reduce((sum, value) => sum + value, 0);
      console.log(columnValues);

      averageValues.push();
   }
   return averageValues;
};
export default calculateAverageValues;

