import React from "react";
import { useDispatch } from "react-redux";
import { incrementInput } from "../../../../redux/actions";

const InputControl = ({ inputId, type }) => {
   const dispatch = useDispatch();
   const s=inputId
   return (
      <div
         className="input-control"
         onClick={() => {
            if (type === "+") {
               console.log(s);
               dispatch(incrementInput(inputId));
            }
         }}
      >
         {type}
      </div>
   );
};

export default InputControl;
