import React from "react";
import { useDispatch, useSelector } from "react-redux";
import {
   incrementCell,
   resetNearestIds,
   setNearestIds,
} from "../../../../../../redux/actions";

const IncrementCell = ({ id, value }) => {
   const nearestIds = useSelector((state) => state.matrix.nearestIds);
   const dispatch = useDispatch();
   const sameValueStyle = {
      backgroundColor: "green",
   };
   const nearestValueStyle = {
      backgroundColor: "aqua",
   };
   const style =
      nearestIds.sameValueIds.length > 0 &&
      nearestIds.sameValueIds.find((sameValueId) => sameValueId === id)
         ? sameValueStyle
         : nearestIds.ids.length > 0 &&
           nearestIds.ids.find((nearestId) => nearestId === id)
         ? nearestValueStyle
         : null;

   return (
      <td style={style}>
         <div
            onMouseEnter={() => dispatch(setNearestIds(id))}
            onMouseLeave={() => dispatch(resetNearestIds())}
            onClick={() => dispatch(incrementCell(id, value))}
         >
            {value}
         </div>
      </td>
   );
};

export default IncrementCell;
