import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";

const AverageRow = () => {
   const data = useSelector((state) =>
      state.matrix.data.map((item) =>
         item.filter((data) => data.value).map((row) => row.value)
      )
   );
   const columnsCount = useSelector((state) => state.matrix.settings.columns);
   const [averageValues, setAverageValues] = useState([]);

   useEffect(() => {
      const averageSum = [];
      const columns = data.reduce((sum, current) => {
         console.log(sum)
         return sum.map((item, index) => item + current[index]);
      });
      console.log(columns);
   }, [data]);

   return <tr></tr>;
};

export default AverageRow;
