import React, { useMemo } from "react";
import { useSelector } from "react-redux";
import StaticCell from "../TableRow/TableCell/StaticCell";

const AverageRow = () => {
   const data = useSelector((state) =>
      state.matrix.data.map((item) =>
         item.filter((data) => data.value).map((row) => row.value)
      )
   );
   //current row values and next row values return the sum of values by equal index
   const averageValues = useMemo(
      () =>
         data
            .reduce((currentRow, nextRow) =>
               currentRow.map((value, index) => value + nextRow[index])
            )
            .map((value) => value / data.length),
      [data]
   );
   const getAvgRowLabel = () => <StaticCell key="avg" value="Avg" />;
   const getAvgRowCells = () =>
      averageValues.map((value, index) => (
         <StaticCell key={index} value={value.toFixed(2)} />
      ));
   const cells = [getAvgRowLabel(), ...getAvgRowCells()];

   return <tr>{cells}</tr>;
};

export default AverageRow;
